import React, { useEffect, useState } from "react";
import { API_URL } from './API/api-iss';
import MapChart from "./MapChart";
import {
  ComposableMap,
  Geographies,
  Geography,
  Marker
} from "react-simple-maps";


export default function WhereTheIss(){

    const [issData, setIssData] = useState(null);


    const geoUrl =
  "https://raw.githubusercontent.com/zcreativelabs/react-simple-maps/master/topojson-maps/world-110m.json";

const MapChart = () => {
  return (
    <ComposableMap>
      <Geographies geography={geoUrl}>
        {({ geographies }) =>
          geographies.map(geo => <Geography key={geo.rsmKey} geography={geo} />)
        }
      </Geographies>
      <Marker coordinates={[-74.006, 50.7128]}>
        <circle r={3} fill="#F53" />
      </Marker>
    </ComposableMap>
  );
};
    useEffect(() => {
        fetchPhoto();

        async function fetchPhoto() {
          const res = await fetch(
            API_URL
          );
          const data = await res.json();
          setIssData(data);
        }
      }, []);


      if (!issData) return <div />;    


    return(
        <div className="photo-nasa">
        <img
          src={issData.url}
          alt={issData.title}
          className="photo"
        />
      <div>
        <h1>International Space Station</h1>
        <p>Visibilit√© : {issData.visibility}</p>
        <p>Latitude : {issData.latitude}</p>
        <p>Longitude : {issData.longitude}</p>
        <p>Altitude : {issData.altitude}</p>
        <p>Vitesse : {issData.velocity} km/h</p>
        <p className="date">{issData.visibility}</p>
        < MapChart />
        export let LAT = {issData.latitude};
        export let LONG = {issData.longitude};

      </div>
    </div>

    );
}
